import React, { useState } from 'react';
import './App.css';

const App = () => {
  // State variables
  const [activeTab, setActiveTab] = useState('home');
  const [intimacyDates, setIntimacyDates] = useState([]);
  const [notes, setNotes] = useState([]);
  const [currentNote, setCurrentNote] = useState('');
  const [partner1Status, setPartner1Status] = useState({ emoji: 'ðŸ˜Š', text: 'Feeling good!' });
  const [partner2Status, setPartner2Status] = useState({ emoji: 'ðŸ’¤', text: 'Sleepy...' });
  const [isHachiDancing, setIsHachiDancing] = useState(false);
  const [isTobiDancing, setIsTobiDancing] = useState(false);
  const [drawingColor, setDrawingColor] = useState('#000000');
  const [isDrawing, setIsDrawing] = useState(false);
  const [drawings, setDrawings] = useState([]);
  const [currentPath, setCurrentPath] = useState([]);
  const [images, setImages] = useState([]);
  const [skateboardTricks, setSkateboardTricks] = useState([
    { name: 'Ollie', mastered: true, notes: 'Worked on height' },
    { name: 'Kickflip', mastered: false, notes: 'Still practicing' }
  ]);

  // Functions
  const addIntimacyDate = () => {
    const today = new Date().toISOString().split('T')[0];
    setIntimacyDates([...intimacyDates, today]);
  };

  const addNote = () => {
    if (currentNote.trim()) {
      setNotes([...notes, { id: Date.now(), text: currentNote, date: new Date() }]);
      setCurrentNote('');
    }
  };

  const petTobi = () => {
    setIsTobiDancing(true);
    setTimeout(() => setIsTobiDancing(false), 3000);
  };

  const petHachi = () => {
    setIsHachiDancing(true);
    setTimeout(() => setIsHachiDancing(false), 3000);
  };

  const startDrawing = (e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setCurrentPath([[x, y]]);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setCurrentPath([...currentPath, [x, y]]);
  };

  const finishDrawing = () => {
    setDrawings([...drawings, { path: currentPath, color: drawingColor }]);
    setCurrentPath([]);
    setIsDrawing(false);
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const newImages = files.map(file => ({
      id: Date.now() + Math.random(),
      url: URL.createObjectURL(file),
      name: file.name
    }));
    setImages([...images, ...newImages]);
  };

  const updatePartner1Status = (e) => {
    setPartner1Status({ ...partner1Status, text: e.target.value });
  };

  const updatePartner2Status = (e) => {
    setPartner2Status({ ...partner2Status, text: e.target.value });
  };

  const chooseEmoji1 = (emoji) => {
    setPartner1Status({ ...partner1Status, emoji });
  };

  const chooseEmoji2 = (emoji) => {
    setPartner2Status({ ...partner2Status, emoji });
  };

  // Icons as SVG components
  const HeartIcon = ({ fill = "red", size = 20 }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 5.42 4.42 3 7.5 3c1.74 0 3.41.81 4.5 2.09C13.09 3.81 14.76 3 16.5 3 19.58 3 22 5.42 22 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z" fill={fill} />
    </svg>
  );

  const MessageSquareIcon = ({ size = 20 }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M21 15a2 2 0 0 1-2 2H7l-4 4V5a2 2 0 0 1 2-2h14a2 2 0 0 1 2 2v10z" stroke="#663399" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" />
    </svg>
  );

  const PawPrintIcon = ({ size = 20 }) => (
    <svg width={size} height={size} viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <circle cx="8" cy="7" r="3" fill="#22c55e" />
      <circle cx="16" cy="7" r="3" fill="#22c55e" />
      <circle cx="5.5" cy="15" r="2.5" fill="#22c55e" />
      <circle cx="18.5" cy="15" r="2.5" fill="#22c55e" />
      <path d="M12 14.5C14.5 14.5 17 16.5 16.5 19C16 21.5 14 22 12 22C10 22 8 21.5 7.5 19C7 16.5 9.5 14.5 12 14.5Z" fill="#22c55e" />
    </svg>
  );

  // Render content based on active tab
  const renderContent = () => {
    switch(activeTab) {
      case 'home':
        return (
          <div className="tab-content">
            <h2 className="tab-title">.:*~* Our Special Space *~*:.</h2>

            <div className="status-box">
              <div className="status-header">
                â˜†â˜†â˜† TODAY'S STATUS â˜†â˜†â˜†
              </div>
              <div className="status-content">
                <div className="status-partner">
                  <div className="status-emoji">{partner1Status.emoji}</div>
                  <p className="status-name">::::: Partner 1 :::::</p>
                  <p className="status-text">{partner1Status.text}</p>
                </div>
                <div className="status-partner">
                  <div className="status-emoji">{partner2Status.emoji}</div>
                  <p className="status-name">::::: Partner 2 :::::</p>
                  <p className="status-text">{partner2Status.text}</p>
                </div>
              </div>
            </div>

            {/* Featured Content Section */}
            <div className="featured-content">
              <div className="section-title">Our Recent Highlights</div>

              {/* Calendar Preview */}
              <div className="feature-preview calendar-preview">
                <div className="preview-header">
                  <HeartIcon size={20} fill="#ff3399" />
                  <h3 className="preview-title">Special Days</h3>
                  <button 
                    className="view-more-btn"
                    onClick={() => setActiveTab('calendar')}
                  >
                    See All â†’
                  </button>
                </div>
                <div className="mini-calendar">
                  <div className="mini-days">
                    {['S','M','T','W','T','F','S'].map((day, i) => (
                      <div key={`day-${i}`} className="mini-day-header">{day}</div>
                    ))}
                    {Array(14).fill(null).map((_, i) => {
                      const day = i + 1;
                      const date = `2025-05-${day.toString().padStart(2, '0')}`;
                      const hasIntimacy = intimacyDates.includes(date);
                      return (
                        <div 
                          key={`mini-day-${i}`} 
                          className={`mini-day ${hasIntimacy ? 'mini-day-marked' : ''}`}
                        >
                          {day}
                          {hasIntimacy && <div className="mini-heart">â™¥</div>}
                        </div>
                      );
                    })}
                  </div>
                  <div className="calendar-description">
                    Track our special moments together! Mark days with hearts to remember them.
                  </div>
                </div>
              </div>

              {/* Message Preview */}
              <div className="feature-preview messages-preview">
                <div className="preview-header">
                  <MessageSquareIcon size={20} />
                  <h3 className="preview-title">Recent Messages</h3>
                  <button 
                    className="view-more-btn"
                    onClick={() => setActiveTab('messages')}
                  >
                    See All â†’
                  </button>
                </div>
                <div className="mini-messages">
                  {notes.length > 0 ? (
                    <div className="mini-note">
                      <p className="mini-note-text">{notes[notes.length - 1].text}</p>
                      <p className="mini-note-date">
                        Posted: {new Date(notes[notes.length - 1].date).toLocaleDateString()}
                      </p>
                    </div>
                  ) : (
                    <div className="no-messages-preview">
                      No messages yet! Leave a sweet note for your partner.
                    </div>
                  )}
                  <button 
                    className="quick-post-btn"
                    onClick={() => setActiveTab('messages')}
                  >
                    Post a Message
                  </button>
                </div>
              </div>

              {/* Pets Preview */}
              <div className="feature-preview pets-preview">
                <div className="preview-header">
                  <PawPrintIcon size={20} />
                  <h3 className="preview-title">Tobi & Hachi</h3>
                  <button 
                    className="view-more-btn"
                    onClick={() => setActiveTab('pets')}
                  >
                    Visit â†’
                  </button>
                </div>
                <div className="mini-pets">
                  <div className="mini-pet-icons">
                    <div 
                      className={`mini-pet-icon tobi ${isTobiDancing ? 'mini-dancing' : ''}`}
                      onClick={petTobi}
                    >
                      <div className="mini-face tobi-mini"></div>
                    </div>
                    <div 
                      className={`mini-pet-icon hachi ${isHachiDancing ? 'mini-dancing' : ''}`}
                      onClick={petHachi}
                    >
                      <div className="mini-face hachi-mini"></div>
                    </div>
                  </div>
                  <div className="pets-description">
                    Click on Tobi & Hachi to see them dance! Visit the pets page to play with them.
                  </div>
                </div>
              </div>

              {/* Drawing Preview */}
              <div className="feature-preview drawing-preview">
                <div className="preview-header">
                  <span className="icon-placeholder">âœŽ</span>
                  <h3 className="preview-title">Draw Together</h3>
                  <button 
                    className="view-more-btn"
                    onClick={() => setActiveTab('drawing')}
                  >
                    Draw Now â†’
                  </button>
                </div>
                <div className="mini-drawing">
                  <div className="mini-canvas">
                    <svg width="100%" height="100%" viewBox="0 0 100 60">
                      <path d="M10,40 C20,20 30,50 40,30 C50,10 60,40 70,20 C80,40 90,30" 
                        stroke="#ff6699" strokeWidth="2" fill="none" strokeLinecap="round"/>
                    </svg>
                  </div>
                  <div className="drawing-description">
                    Create sweet drawings for each other in our shared drawing board!
                  </div>
                </div>
              </div>
            </div>

            <div className="status-update-box">
              <h3 className="status-update-title">.:*~* Update Your Status *~*:.</h3>

              <div className="status-update-grid">
                <div className="status-update-partner">
                  <p className="status-update-name">Partner 1</p>
                  <div className="emoji-picker">
                    {['ðŸ˜Š', 'â¤ï¸', 'ðŸ˜´', 'ðŸ¥°', 'ðŸ˜¢', 'ðŸ’ª'].map(emoji => (
                      <button 
                        key={emoji} 
                        onClick={() => chooseEmoji1(emoji)}
                        className="emoji-button"
                      >
                        {emoji}
                      </button>
                    ))}
                  </div>
                  <input 
                    type="text" 
                    value={partner1Status.text}
                    onChange={updatePartner1Status}
                    className="status-input"
                    placeholder="How are you feeling?"
                  />
                </div>
                <div className="status-update-partner">
                  <p className="status-update-name">Partner 2</p>
                  <div className="emoji-picker">
                    {['ðŸ˜Š', 'â¤ï¸', 'ðŸ˜´', 'ðŸ¥°', 'ðŸ˜¢', 'ðŸ’ª'].map(emoji => (
                      <button 
                        key={emoji} 
                        onClick={() => chooseEmoji2(emoji)}
                        className="emoji-button"
                      >
                        {emoji}
                      </button>
                    ))}
                  </div>
                  <input 
                    type="text" 
                    value={partner2Status.text}
                    onChange={updatePartner2Status}
                    className="status-input"
                    placeholder="How are you feeling?"
                  />
                </div>
              </div>

              <div className="status-reminder">
                â™¡â™¡â™¡ Don't forget to update your status to let your partner know how you're feeling today! â™¡â™¡â™¡
              </div>
            </div>
          </div>
        );

      case 'calendar':
        return (
          <div className="tab-content">
            <h2 className="tab-title">.:*~* Intimacy Calendar *~*:.</h2>
            <div className="calendar-box">
              <div className="calendar-header">â˜… May 2025 â˜…</div>

              <div className="calendar-grid">
                {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                  <div key={day} className="calendar-day-header">{day}</div>
                ))}
                {Array(31).fill(null).map((_, i) => {
                  const day = i + 1;
                  const date = `2025-05-${day.toString().padStart(2, '0')}`;
                  const hasIntimacy = intimacyDates.includes(date);
                  return (
                    <div 
                      key={day} 
                      className={`calendar-day ${hasIntimacy ? 'calendar-day-marked' : ''}`}
                      onClick={() => {
                        if (hasIntimacy) {
                          setIntimacyDates(intimacyDates.filter(d => d !== date));
                        } else {
                          setIntimacyDates([...intimacyDates, date]);
                        }
                      }}
                    >
                      {day}
                      {hasIntimacy && (
                        <div className="heart-indicator">
                          <HeartIcon fill="#ec4899" />
                        </div>
                      )}
                    </div>
                  );
                })}
              </div>

              <div className="calendar-footer">
                <button 
                  onClick={addIntimacyDate}
                  className="mark-today-button"
                >
                  <HeartIcon size={16} /> Mark Today! <HeartIcon size={16} />
                </button>
                <div className="calendar-tip">
                  Click on a day to mark or unmark it. Special days will show with a heart! â™¥
                </div>
              </div>
            </div>
          </div>
        );

      case 'messages':
        return (
          <div className="tab-content">
            <h2 className="tab-title">.:*~* Message Board *~*:.</h2>
            <div className="message-box">
              <div className="message-header">â˜… Leave a Sweet Message â˜…</div>
              <textarea
                value={currentNote}
                onChange={(e) => setCurrentNote(e.target.value)}
                placeholder="Write a sweet note for your partner..."
                className="message-textarea"
                rows="3"
              />
              <div className="message-button-container">
                <button 
                  onClick={addNote}
                  className="post-button"
                >
                  Post Note!
                </button>
              </div>
            </div>

            <div className="notes-grid">
              {notes.map(note => (
                <div 
                  key={note.id} 
                  className="note-card"
                >
                  <p className="note-text">{note.text}</p>
                  <p className="note-date">
                    Posted: {new Date(note.date).toLocaleDateString()} at {new Date(note.date).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                  </p>
                  <div className="note-heart">
                    <HeartIcon size={16} />
                  </div>
                </div>
              ))}
              {notes.length === 0 && (
                <div className="no-notes-message">
                  <MessageSquareIcon size={48} />
                  <p>No messages yet! Be the first to post something sweet!</p>
                </div>
              )}
            </div>
          </div>
        );

      case 'pets':
        return (
          <div className="tab-content">
            <h2 className="tab-title">.:*~* Our Furry Friends *~*:.</h2>
            <div className="pets-container">
              <div className="pet-card">
                <div 
                  className={`pet-image tobi ${isTobiDancing ? 'dancing' : ''}`}
                  onClick={petTobi}
                >
                  <div className="pet-face tobi-face">
                    <div className="pet-eye left"></div>
                    <div className="pet-eye right"></div>
                    <div className="pet-nose"></div>
                  </div>
                </div>
                <div className="pet-name-tag tobi-tag">
                  <h3>â˜… Tobi â˜…</h3>
                  <p>Click to pet me!</p>
                </div>
              </div>

              <div className="pet-card">
                <div 
                  className={`pet-image hachi ${isHachiDancing ? 'dancing' : ''}`}
                  onClick={petHachi}
                >
                  <div className="pet-face hachi-face">
                    <div className="pet-eye left"></div>
                    <div className="pet-eye right"></div>
                    <div className="pet-nose"></div>
                  </div>
                </div>
                <div className="pet-name-tag hachi-tag">
                  <h3>â˜… Hachi â˜…</h3>
                  <p>Click to pet me!</p>
                </div>
              </div>
            </div>
          </div>
        );

      case 'drawing':
        return (
          <div className="tab-content">
            <h2 className="tab-title">.:*~* Draw 4 Me! *~*:.</h2>
            <div className="drawing-controls">
              <div className="color-picker">
                <label>Color:</label>
                <input 
                  type="color" 
                  value={drawingColor}
                  onChange={(e) => setDrawingColor(e.target.value)}
                  className="color-input"
                />
              </div>
              <button 
                onClick={() => setDrawings([])}
                className="clear-button"
              >
                Clear Canvas
              </button>
            </div>

            <div 
              className="drawing-canvas"
              onMouseDown={startDrawing}
              onMouseMove={draw}
              onMouseUp={finishDrawing}
              onMouseLeave={finishDrawing}
            >
              <svg width="100%" height="100%">
                {drawings.map((drawing, index) => (
                  <polyline
                    key={index}
                    points={drawing.path.map(point => point.join(',')).join(' ')}
                    fill="none"
                    stroke={drawing.color}
                    strokeWidth="3"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                ))}
                {currentPath.length > 0 && (
                  <polyline
                    points={currentPath.map(point => point.join(',')).join(' ')}
                    fill="none"
                    stroke={drawingColor}
                    strokeWidth="3"
                    strokeLinecap="round"
                    strokeLinejoin="round"
                  />
                )}
              </svg>
            </div>
            <div className="drawing-tip">
              Draw something sweet for your partner! They'll love it! â™¥
            </div>
          </div>
        );

      default:
        return (
          <div className="tab-content">
            <h2 className="tab-title">Welcome to our website!</h2>
            <p className="centered-text">Please select a tab from the navigation bar above.</p>
          </div>
        );
    }
  };

  return (
    <div className="container">
      <header className="header">
        <div className="new-badge">NEW!</div>
        <h1>Our Special Connection</h1>
        <p className="tagline">...distance means so little when you mean so much...</p>
        <p className="welcome-text">Welcome to our love nest on the World Wide Web! â™¥â™¥â™¥ Est. 2025 â™¥â™¥â™¥</p>
      </header>

      <div className="nav-bar">
        <div className="nav-title">:: NAVIGATION BAR ::</div>
        <ul className="nav-buttons">
          <li>
            <button 
              className={`nav-button ${activeTab === 'home' ? 'active-button' : ''}`}
              onClick={() => setActiveTab('home')}
            >
              â˜… Home â˜…
            </button>
          </li>
          <li>
            <button 
              className={`nav-button ${activeTab === 'calendar' ? 'active-button' : ''}`}
              onClick={() => setActiveTab('calendar')}
            >
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <HeartIcon size={16} fill={activeTab === 'calendar' ? '#ec4899' : '#fff'} />
                <span style={{ marginLeft: '4px' }}>Calendar</span>
              </div>
            </button>
          </li>
          <li>
            <button 
              className={`nav-button ${activeTab === 'messages' ? 'active-button' : ''}`}
              onClick={() => setActiveTab('messages')}
            >
              <div style={{ display: 'flex', alignItems: 'center' }}>
                <MessageSquareIcon size={16} />
                <span style={{ marginLeft: '4px' }}>Messages</span>
              </div>
            </button>
          </li>
          <li>
