import React, { useState } from 'react';
import { Heart, MessageSquare, Image, Activity, PawPrint, Clock } from 'lucide-react';

const App = () => {
  // State variables
  const [activeTab, setActiveTab] = useState('home');
  const [intimacyDates, setIntimacyDates] = useState([]);
  const [notes, setNotes] = useState([]);
  const [currentNote, setCurrentNote] = useState('');
  const [partner1Status, setPartner1Status] = useState({ emoji: '😊', text: 'Feeling good!' });
  const [partner2Status, setPartner2Status] = useState({ emoji: '💤', text: 'Sleepy...' });
  const [isHachiDancing, setIsHachiDancing] = useState(false);
  const [isTobiDancing, setIsTobiDancing] = useState(false);
  const [drawingColor, setDrawingColor] = useState('#000000');
  const [isDrawing, setIsDrawing] = useState(false);
  const [drawings, setDrawings] = useState([]);
  const [currentPath, setCurrentPath] = useState([]);
  const [images, setImages] = useState([]);

  // Functions
  const addIntimacyDate = () => {
    const today = new Date().toISOString().split('T')[0];
    setIntimacyDates([...intimacyDates, today]);
  };

  const addNote = () => {
    if (currentNote.trim()) {
      setNotes([...notes, { id: Date.now(), text: currentNote, date: new Date() }]);
      setCurrentNote('');
    }
  };

  const petTobi = () => {
    setIsTobiDancing(true);
    setTimeout(() => setIsTobiDancing(false), 3000);
  };

  const petHachi = () => {
    setIsHachiDancing(true);
    setTimeout(() => setIsHachiDancing(false), 3000);
  };

  const startDrawing = (e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setCurrentPath([[x, y]]);
    setIsDrawing(true);
  };

  const draw = (e) => {
    if (!isDrawing) return;
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    setCurrentPath([...currentPath, [x, y]]);
  };

  const finishDrawing = () => {
    setDrawings([...drawings, { path: currentPath, color: drawingColor }]);
    setCurrentPath([]);
    setIsDrawing(false);
  };

  const handleImageUpload = (e) => {
    const files = Array.from(e.target.files);
    const newImages = files.map(file => ({
      id: Date.now() + Math.random(),
      url: URL.createObjectURL(file),
      name: file.name
    }));
    setImages([...images, ...newImages]);
  };

  const updatePartner1Status = (e) => {
    setPartner1Status({ ...partner1Status, text: e.target.value });
  };

  const updatePartner2Status = (e) => {
    setPartner2Status({ ...partner2Status, text: e.target.value });
  };

  const chooseEmoji1 = (emoji) => {
    setPartner1Status({ ...partner1Status, emoji });
  };

  const chooseEmoji2 = (emoji) => {
    setPartner2Status({ ...partner2Status, emoji });
  };

  // Render
  return (
    <div className="max-w-4xl mx-auto p-4" style={{ fontFamily: 'Comic Sans MS, cursive', backgroundImage: 'url(/api/placeholder/100/100)', backgroundRepeat: 'repeat' }}>
      <header className="text-center mb-6 relative">
        <div className="bg-yellow-400 border-4 border-blue-500 rounded-lg p-4 shadow-lg transform -rotate-1">
          <h1 className="text-3xl font-bold text-pink-600 mb-2" style={{ textShadow: '2px 2px 0 #fff, 4px 4px 0 #000' }}>*~*~* Our Special Connection *~*~*</h1>
          <p className="text-purple-700 italic mt-2">...distance means so little when you mean so much...</p>
          <div className="text-blue-600 font-bold mt-2 overflow-hidden">
            <div className="whitespace-nowrap">Welcome to our love nest on the World Wide Web! ♥♥♥ Est. 2025 ♥♥♥</div>
          </div>
        </div>
        <div className="absolute -top-6 -left-6 bg-red-400 rounded-full w-16 h-16 flex items-center justify-center transform rotate-12 border-2 border-white shadow-lg">
          <p className="text-white font-bold text-sm">NEW!</p>
        </div>
      </header>

      <div className="bg-teal-200 rounded-lg shadow-lg overflow-hidden mb-6 border-4 border-purple-400">
        <nav className="bg-gradient-to-r from-yellow-400 via-pink-500 to-blue-500 p-2">
          <div className="text-center text-white font-bold mb-2">:: NAVIGATION BAR ::</div>
          <ul className="flex flex-wrap">
            <li className="mr-1 mb-1">
              <button 
                onClick={() => setActiveTab('home')}
                className={`py-2 px-4 rounded-t ${activeTab === 'home' ? 'bg-white text-blue-600 font-bold border-2 border-blue-500 border-b-0' : 'bg-blue-300 text-white border border-blue-600'}`}
                style={{ boxShadow: '2px -2px 5px rgba(0,0,0,0.3)' }}
              >
                ★ Home ★
              </button>
            </li>
            <li className="mr-1 mb-1">
              <button 
                onClick={() => setActiveTab('calendar')}
                className={`py-2 px-4 rounded-t ${activeTab === 'calendar' ? 'bg-white text-pink-600 font-bold border-2 border-pink-500 border-b-0' : 'bg-pink-300 text-white border border-pink-600'}`}
                style={{ boxShadow: '2px -2px 5px rgba(0,0,0,0.3)' }}
              >
                <span className="flex items-center">
                  <Heart size={16} className="mr-1" fill={activeTab === 'calendar' ? '#ec4899' : '#fff'} /> Calendar
                </span>
              </button>
            </li>
            <li className="mr-1 mb-1">
              <button 
                onClick={() => setActiveTab('notes')}
                className={`py-2 px-4 rounded-t ${activeTab === 'notes' ? 'bg-white text-purple-600 font-bold border-2 border-purple-500 border-b-0' : 'bg-purple-300 text-white border border-purple-600'}`}
                style={{ boxShadow: '2px -2px 5px rgba(0,0,0,0.3)' }}
              >
                <span className="flex items-center">
                  <MessageSquare size={16} className="mr-1" /> Messages
                </span>
              </button>
            </li>
            <li className="mr-1 mb-1">
              <button 
                onClick={() => setActiveTab('pets')}
                className={`py-2 px-4 rounded-t ${activeTab === 'pets' ? 'bg-white text-green-600 font-bold border-2 border-green-500 border-b-0' : 'bg-green-300 text-white border border-green-600'}`}
                style={{ boxShadow: '2px -2px 5px rgba(0,0,0,0.3)' }}
              >
                <span className="flex items-center">
                  <PawPrint size={16} className="mr-1" /> Our Pets
                </span>
              </button>
            </li>
          </ul>
        </nav>

        <div className="min-h-96 p-2 bg-white border-l-4 border-r-4 border-b-4 border-purple-400">
          {activeTab === 'home' && (
            <div className="p-4 bg-pink-100 rounded-lg">
              <h2 className="text-2xl font-bold text-center mb-6 text-pink-700" style={{ textShadow: '1px 1px 0 #fff' }}>
                .:*~* Our Special Space *~*:.
              </h2>

              <table className="w-full border-collapse border-2 border-blue-400 mb-6" cellPadding="8" cellSpacing="0">
                <tbody>
                  <tr className="bg-gradient-to-r from-pink-300 to-purple-300">
                    <td colSpan="2" className="border-2 border-blue-400 text-center text-lg font-bold">
                      ☆☆☆ TODAY'S STATUS ☆☆☆
                    </td>
                  </tr>
                  <tr>
                    <td className="border-2 border-blue-400 text-center bg-yellow-100 w-1/2">
                      <div className="text-4xl mb-2">{partner1Status.emoji}</div>
                      <p className="font-bold text-blue-700">::::: Partner 1 :::::</p>
                      <p className="bg-white p-2 mt-1 border border-gray-300 rounded">{partner1Status.text}</p>
                    </td>
                    <td className="border-2 border-blue-400 text-center bg-blue-100 w-1/2">
                      <div className="text-4xl mb-2">{partner2Status.emoji}</div>
                      <p className="font-bold text-purple-700">::::: Partner 2 :::::</p>
                      <p className="bg-white p-2 mt-1 border border-gray-300 rounded">{partner2Status.text}</p>
                    </td>
                  </tr>
                </tbody>
              </table>

              <div className="grid grid-cols-2 gap-4 mb-6">
                <div 
                  className="bg-blue-200 p-4 rounded-lg text-center cursor-pointer hover:bg-blue-300 transition border-2 border-blue-500"
                  onClick={() => setActiveTab('calendar')}
                  style={{ boxShadow: '3px 3px 0 #3b82f6' }}
                >
                  <Heart size={32} className="mx-auto mb-2 text-blue-600" />
                  <p className="font-bold">Intimacy Calendar!</p>
                </div>
                <div 
                  className="bg-purple-200 p-4 rounded-lg text-center cursor-pointer hover:bg-purple-300 transition border-2 border-purple-500"
                  onClick={() => setActiveTab('notes')}
                  style={{ boxShadow: '3px 3px 0 #8b5cf6' }}
                >
                  <MessageSquare size={32} className="mx-auto mb-2 text-purple-600" />
                  <p className="font-bold">Message Board</p>
                </div>
                <div 
                  className="bg-green-200 p-4 rounded-lg text-center cursor-pointer hover:bg-green-300 transition border-2 border-green-500"
                  onClick={() => setActiveTab('pets')}
                  style={{ boxShadow: '3px 3px 0 #22c55e' }}
                >
                  <PawPrint size={32} className="mx-auto mb-2 text-green-600" />
                  <p className="font-bold">Pet Corner</p>
                </div>
                <div 
                  className="bg-yellow-200 p-4 rounded-lg text-center cursor-pointer hover:bg-yellow-300 transition border-2 border-yellow-500"
                  onClick={() => setActiveTab('drawing')}
                  style={{ boxShadow: '3px 3px 0 #eab308' }}
                >
                  <Image size={32} className="mx-auto mb-2 text-yellow-600" />
                  <p className="font-bold">Draw 4 Me!</p>
                </div>
              </div>

              <div className="text-center p-4 bg-green-100 rounded-lg border-2 border-dashed border-green-500">
                <h3 className="font-bold mb-2 bg-green-300 p-1 text-green-800">.:*~* Update Your Status *~*:.</h3>
                <table className="w-full border-collapse mb-4" cellPadding="4">
                  <tbody>
                    <tr>
                      <td className="w-1/2">
                        <p className="mb-1 font-bold text-blue-600 text-center">Partner 1</p>
                        <div className="flex justify-center space-x-2 mb-2 bg-white p-2 rounded border border-blue-300">
                          {['😊', '❤️', '😴', '🥰', '😢', '💪'].map(emoji => (
                            <button 
                              key={emoji} 
                              onClick={() => chooseEmoji1(emoji)}
                              className="text-2xl hover:bg-gray-100 p-1 rounded"
                            >
                              {emoji}
                            </button>
                          ))}
                        </div>
                        <input 
                          type="text" 
                          value={partner1Status.text}
                          onChange={updatePartner1Status}
                          className="border-2 border-blue-400 rounded py-1 px-2 w-full"
                          placeholder="How are you feeling?"
                        />
                      </td>
                      <td className="w-1/2">
                        <p className="mb-1 font-bold text-purple-600 text-center">Partner 2</p>
                        <div className="flex justify-center space-x-2 mb-2 bg-white p-2 rounded border border-purple-300">
                          {['😊', '❤️', '😴', '🥰', '😢', '💪'].map(emoji => (
                            <button 
                              key={emoji} 
                              onClick={() => chooseEmoji2(emoji)}
                              className="text-2xl hover:bg-gray-100 p-1 rounded"
                            >
                              {emoji}
                            </button>
                          ))}
                        </div>
                        <input 
                          type="text" 
                          value={partner2Status.text}
                          onChange={updatePartner2Status}
                          className="border-2 border-purple-400 rounded py-1 px-2 w-full"
                          placeholder="How are you feeling?"
                        />
                      </td>
                    </tr>
                  </tbody>
                </table>
                <div className="bg-pink-100 p-2 rounded border border-pink-300 overflow-hidden">
                  <div className="whitespace-nowrap">
                    ♡♡♡ Don't forget to update your status to let your partner know how you're feeling today! ♡♡♡
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'calendar' && (
            <div className="p-4 bg-blue-100 rounded-lg">
              <h2 className="text-2xl font-bold text-center mb-6 text-blue-700" style={{ textShadow: '1px 1px 0 #fff' }}>
                .:*~* Intimacy Calendar *~*:.
              </h2>
              <div className="bg-white p-4 rounded-lg shadow mb-4 border-4 border-blue-300">
                <div className="mb-2 text-center bg-blue-200 p-2 font-bold text-blue-800 rounded">
                  ★ May 2025 ★
                </div>
                <div className="grid grid-cols-7 gap-1 mb-4">
                  {['Sun', 'Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat'].map(day => (
                    <div key={day} className="text-center font-bold p-2 bg-gradient-to-b from-blue-200 to-blue-300 border border-blue-400">
                      {day}
                    </div>
                  ))}
                  {Array(31).fill(null).map((_, i) => {
                    const day = i + 1;
                    const date = `2025-05-${day.toString().padStart(2, '0')}`;
                    const hasIntimacy = intimacyDates.includes(date);
                    return (
                      <div 
                        key={day} 
                        className={`text-center p-2 border-2 ${hasIntimacy ? 'bg-pink-200 border-pink-500' : 'bg-white border-gray-300 hover:bg-gray-100'}`}
                        style={{ fontWeight: 'bold', position: 'relative' }}
                        onClick={() => {
                          if (hasIntimacy) {
                            setIntimacyDates(intimacyDates.filter(d => d !== date));
                          } else {
                            setIntimacyDates([...intimacyDates, date]);
                          }
                        }}
                      >
                        {day}
                        {hasIntimacy && (
                          <div className="absolute inset-0 flex items-center justify-center">
                            <Heart size={20} className="text-pink-600" fill="#ec4899" />
                          </div>
                        )}
                      </div>
                    );
                  })}
                </div>
                <div className="text-center">
                  <button 
                    onClick={addIntimacyDate}
                    className="bg-pink-500 text-white py-2 px-6 rounded hover:bg-pink-600 transition flex items-center mx-auto font-bold border-2 border-pink-700"
                    style={{ boxShadow: '3px 3px 0 #be185d' }}
                  >
                    <Heart size={16} className="mr-2" fill="#fff" /> Mark Today! <Heart size={16} className="ml-2" fill="#fff" />
                  </button>
                  <div className="mt-4 text-center text-sm italic text-blue-800">
                    Click on a day to mark or unmark it. Special days will show with a heart! ♥
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'notes' && (
            <div className="p-4 bg-purple-100 rounded-lg">
              <h2 className="text-2xl font-bold text-center mb-6 text-purple-700" style={{ textShadow: '1px 1px 0 #fff' }}>
                .:*~* Message Board *~*:.
              </h2>
              <div className="bg-white p-4 rounded-lg shadow mb-4 border-4 border-purple-300">
                <div className="mb-2 text-center bg-purple-200 p-2 font-bold text-purple-800 rounded">
                  ★ Leave a Sweet Message ★
                </div>
                <textarea
                  value={currentNote}
                  onChange={(e) => setCurrentNote(e.target.value)}
                  placeholder="Write a sweet note for your partner..."
                  className="w-full border-2 border-purple-300 rounded p-2 mb-2"
                  rows="3"
                  style={{ fontFamily: 'Comic Sans MS, cursive' }}
                />
                <div className="text-center">
                  <button 
                    onClick={addNote}
                    className="bg-purple-500 text-white py-2 px-6 rounded hover:bg-purple-600 transition mx-auto font-bold border-2 border-purple-700"
                    style={{ boxShadow: '3px 3px 0 #7e22ce' }}
                  >
                    Post Note!
                  </button>
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                {notes.map(note => (
                  <div 
                    key={note.id} 
                    className="bg-yellow-100 p-3 rounded shadow-md transform rotate-1 hover:rotate-0 transition border-2 border-yellow-400"
                    style={{ 
                      minHeight: '120px',
                      boxShadow: '3px 3px 5px rgba(0,0,0,0.2)'
                    }}
                  >
                    <p className="mb-2 font-bold text-purple-800" style={{ fontFamily: 'Comic Sans MS, cursive' }}>{note.text}</p>
                    <p className="text-xs text-gray-500 italic">
                      Posted: {new Date(note.date).toLocaleDateString()} at {new Date(note.date).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
                    </p>
                    <div className="mt-2 flex justify-end">
                      <Heart size={16} className="text-pink-600" fill="#ec4899" />
                    </div>
                  </div>
                ))}
                {notes.length === 0 && (
                  <div className="col-span-2 text-center p-8 border-2 border-dashed rounded border-purple-400 bg-purple-50">
                    <MessageSquare size={48} className="mx-auto mb-2 text-purple-400" />
                    <p className="text-purple-800 italic">No messages yet! Be the first to post something sweet!</p>
                  </div>
                )}
              </div>
            </div>
          )}

          {activeTab === 'pets' && (
            <div className="p-4 bg-green-100 rounded-lg">
              <h2 className="text-2xl font-bold text-center mb-6 text-green-700" style={{ textShadow: '1px 1px 0 #fff' }}>
                .:*~* Our Furry Friends *~*:.
              </h2>
              <div className="flex flex-col md:flex-row justify-center items-center gap-8">
                <div className="text-center">
                  <div 
                    className={`w-64 h-64 bg-yellow-200 rounded-full flex justify-center items-center cursor-pointer mb-4 border-4 border-amber-500 ${isTobiDancing ? 'animate-bounce' : ''}`}
                    onClick={petTobi}
                    style={{ boxShadow: '0 0 15px rgba(245, 158, 11, 0.6)' }}
                  >
                    <div className="relative">
                      <div className="w-32 h-32 bg-amber-700 rounded-full mb-4 relative">
                        <div className="absolute w-8 h-8 bg-white rounded-full top-6 left-6 border border-amber-900"></div>
                        <div className="absolute w-8 h-8 bg-white rounded-full top-6 right-6 border border-amber-900"></div>
                        <div className="absolute w-12 h-6 bg-pink-300 rounded-full bottom-8 left-10 border border-amber-900"></div>
                      </div>
                      <div className="w-12 h-6 bg-amber-700 rounded-full mx-auto"></div>
                    </div>
                  </div>
                  <div className="bg-yellow-300 border-2 border-yellow-500 rounded-lg p-2 transform -rotate-2">
                    <h3 className="text-xl font-bold">★ Tobi ★</h3>
                    <p className="text-gray-700 italic">Click to pet me!</p>
                  </div>
                </div>

                <div className="text-center">
                  <div 
                    className={`w-64 h-64 bg-gray-200 rounded-full flex justify-center items-center cursor-pointer mb-4 border-4 border-gray-500 ${isHachiDancing ? 'animate-bounce' : ''}`}
                    onClick={petHachi}
                    style={{ boxShadow: '0 0 15px rgba(107, 114, 128, 0.6)' }}
                  >
                    <div className="relative">
                      <div className="w-32 h-32 bg-gray-700 rounded-full mb-4 relative">
                        <div className="absolute w-8 h-8 bg-white rounded-full top-6 left-6 border border-gray-900"></div>
                        <div className="absolute w-8 h-8 bg-white rounded-full top-6 right-6 border border-gray-900"></div>
                        <div className="absolute w-12 h-6 bg-pink-300 rounded-full bottom-8 left-10 border border-gray-900"></div>
                      </div>
                      <div className="w-12 h-6 bg-gray-700 rounded-full mx-auto"></div>
                    </div>
                  </div>
                  <div className="bg-gray-300 border-2 border-gray-500 rounded-lg p-2 transform rotate-2">
                    <h3 className="text-xl font-bold">★ Hachi ★</h3>
                    <p className="text-gray-700 italic">Click to pet me!</p>
                  </div>
                </div>
              </div>
            </div>
          )}

          {activeTab === 'drawing' && (
            <div className="p-4 bg-yellow-100 rounded-lg">
              <h2 className="text-2xl font-bold text-center mb-6 text-yellow-700" style={{ textShadow: '1px 1px 0 #fff' }}>
                .:*~* Draw 4 Me! *~*:.
              </h2>
              <div className="mb-4 flex justify-between items-center bg-white p-3 rounded-lg border-4 border-yellow-300">
                <div>
                  <label className="mr-2 font-bold text-yellow-800">Color:</label>
                  <input 
                    type="color" 
                    value={drawingColor}
                    onChange={(e) => setDrawingColor(e.target.value)}
                    className="border-2 border-yellow-400 rounded"
                  />
                </div>
                <button 
                  onClick={() => setDrawings([])}
                  className="bg-red-500 text-white py-1 px-3 rounded hover:bg-red-600 transition font-bold border-2 border-red-700"
                  style={{ boxShadow: '2px 2px 0 #b91c1c' }}
                >
                  Clear Canvas
                </button>
              </div>

              <div 
                className="bg-white border-4 border-yellow-400 rounded-lg"
                style={{ width: '100%', height: '400px', touchAction: 'none' }}
                onMouseDown={startDrawing}
                onMouseMove={draw}
                onMouseUp={finishDrawing}
                onMouseLeave={finishDrawing}
              >
                <svg width="100%" height="100%">
                  {drawings.map((drawing, index) => (
                    <polyline
                      key={index}
                      points={drawing.path.map(point => point.join(',')).join(' ')}
                      fill="none"
                      stroke={drawing.color}
                      strokeWidth="3"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  ))}
                  {currentPath.length > 0 && (
                    <polyline
                      points={currentPath.map(point => point.join(',')).join(' ')}
                      fill="none"
                      stroke={drawingColor}
                      strokeWidth="3"
                      strokeLinecap="round"
                      strokeLinejoin="round"
                    />
                  )}
                </svg>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="flex justify-center space-x-2 mb-6">
        <div className="w-3 h-3 bg-red-500 rounded-full"></div>
        <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
        <div className="w-3 h-3 bg-green-500 rounded-full"></div>
        <div className="w-3 h-3 bg-blue-500 rounded-full"></div>
        <div className="w-3 h-3 bg-purple-500 rounded-full"></div>
      </div>

      <footer className="text-center text-blue-800 text-sm bg-gradient-to-r from-yellow-200 to-pink-200 p-3 border-2 border-blue-400 rounded">
        <p className="mb-1">© 2025 - Our Love Shack on the Information Superhighway</p>
        <p className="text-xs">Best viewed in Internet Explorer 6.0 or Netscape Navigator</p>
        <div className="mt-2 flex justify-center space-x-4">
          <div className="border border-gray-400 bg-gray-200 rounded px-2 py-1 text-xs">
            Visitors: 2,345
          </div>
          <div className="border border-gray-400 bg-gray-200 rounded px-2 py-1 text-xs flex items-center">
            <span className="w-2 h-2 bg-green-500 rounded-full mr-1"></span> Online Now
          </div>
        </div>
      </footer>
    </div>
  );
};

export default App;
