import React, { useState, useEffect } from 'react';

function TrickSection({ currentUser }) {
  const [tricks, setTricks] = useState([]);
  const [newTrick, setNewTrick] = useState('');
  const [difficulty, setDifficulty] = useState('Easy');
  const [description, setDescription] = useState('');
  const [selectedFile, setSelectedFile] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');

  // Load tricks from localStorage on component mount
  useEffect(() => {
    const storedTricks = localStorage.getItem('skateboardingTricks');
    if (storedTricks) {
      setTricks(JSON.parse(storedTricks));
    }
  }, []);

  // Save tricks to localStorage whenever they change
  useEffect(() => {
    localStorage.setItem('skateboardingTricks', JSON.stringify(tricks));
  }, [tricks]);

  const handleFileChange = (e) => {
    if (e.target.files && e.target.files[0]) {
      const file = e.target.files[0];
      setSelectedFile(file);

      // Create a preview URL for the selected image/video
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const addTrick = () => {
    if (!currentUser) {
      alert('Please sign in to add skateboarding tricks!');
      return;
    }

    if (!newTrick.trim()) {
      alert('Please enter a trick name!');
      return;
    }

    const newTrickEntry = {
      id: Date.now(),
      name: newTrick,
      difficulty,
      description,
      media: previewUrl,
      user: currentUser,
      timestamp: new Date().toLocaleString()
    };

    setTricks([newTrickEntry, ...tricks]);
    setNewTrick('');
    setDifficulty('Easy');
    setDescription('');
    setSelectedFile(null);
    setPreviewUrl('');
  };

  const deleteTrick = (id) => {
    setTricks(tricks.filter(trick => trick.id !== id));
  };

  return (
    <div className="container">
      <h2>🛹 Skateboarding Tricks 🛹</h2>

      <div style={{
        border: '2px solid #33cc33',
        padding: '15px',
        backgroundColor: '#f0fff0',
        marginBottom: '20px'
      }}>
        <h3 style={{color: '#006600', textAlign: 'center'}}>Add New Trick</h3>

        <div style={{display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
          <input 
            type="text"
            placeholder="Trick Name"
            value={newTrick}
            onChange={(e) => setNewTrick(e.target.value)}
            disabled={!currentUser}
            style={{margin: '10px 0', width: '80%'}}
          />

          <select
            value={difficulty}
            onChange={(e) => setDifficulty(e.target.value)}
            disabled={!currentUser}
            style={{margin: '10px 0', width: '80%'}}
          >
            <option value="Easy">Easy</option>
            <option value="Medium">Medium</option>
            <option value="Hard">Hard</option>
            <option value="Pro">Pro</option>
          </select>

          <textarea
            placeholder="Describe how to do this trick..."
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            disabled={!currentUser}
            style={{margin: '10px 0', width: '80%', height: '80px'}}
          />

          <input 
            type="file" 
            accept="image/*,video/*" 
            onChange={handleFileChange}
            disabled={!currentUser}
            style={{marginBottom: '10px'}}
          />

          {previewUrl && (
            <div style={{margin: '10px 0'}}>
              <p>Preview:</p>
              {previewUrl.startsWith('data:image') ? (
                <img 
                  src={previewUrl} 
                  alt="Preview" 
                  style={{
                    maxWidth: '200px', 
                    maxHeight: '200px',
                    border: '1px solid #000'
                  }} 
                />
              ) : (
                <video 
                  src={previewUrl} 
                  controls
                  style={{
                    maxWidth: '200px', 
                    maxHeight: '200px',
                    border: '1px solid #000'
                  }}
                />
              )}
            </div>
          )}

          <button 
            onClick={addTrick}
            disabled={!currentUser || !newTrick.trim()}
            style={{marginTop: '10px'}}
          >
            Add Trick
          </button>
        </div>
      </div>

      <div>
        <h3 style={{backgroundColor: '#ccffcc', padding: '5px', textAlign: 'center'}}>
          Trick Collection
        </h3>

        {tricks.length === 0 ? (
          <p style={{textAlign: 'center'}}>No tricks added yet. Show off your skills!</p>
        ) : (
          <div style={{
            display: 'flex',
            flexDirection: 'column',
            gap: '15px'
          }}>
            {tricks.map(trick => (
              <div key={trick.id} style={{
                border: '2px solid #33cc33',
                padding: '15px',
                backgroundColor: '#f8fff8',
                position: 'relative'
              }}>
                <h4 style={{margin: '0 0 10px 0', color: '#006600'}}>{trick.name}</h4>
                <p style={{margin: '5px 0', fontSize: '14px'}}>
                  <strong>Difficulty:</strong> 
                  <span style={{
                    color: trick.difficulty === 'Easy' ? 'green' : 
                           trick.difficulty === 'Medium' ? 'orange' :
                           trick.difficulty === 'Hard' ? 'red' : 'purple'
                  }}>
                    {trick.difficulty}
                  </span>
                </p>
                <p style={{margin: '5px 0', fontSize: '14px'}}>{trick.description}</p>

                {trick.media && (
                  <div style={{margin: '10px 0', textAlign: 'center'}}>
                    {trick.media.startsWith('data:image') ? (
                      <img 
                        src={trick.media} 
                        alt={trick.name}
                        style={{
                          maxWidth: '300px', 
                          maxHeight: '300px',
                          border: '1px solid #000'
                        }} 
                      />
                    ) : (
                      <video 
                        src={trick.media} 
                        controls
                        style={{
                          maxWidth: '300px', 
                          maxHeight: '300px',
                          border: '1px solid #000'
                        }}
                      />
                    )}
                  </div>
                )}

                <p style={{margin: '5px 0', fontSize: '12px'}}>Added by: {trick.user}</p>
                <p style={{margin: '0', fontSize: '10px', color: '#666'}}>{trick.timestamp}</p>

                <button 
                  onClick={() => deleteTrick(trick.id)}
                  style={{
                    position: 'absolute',
                    top: '10px',
                    right: '10px',
                    backgroundColor: '#ff6666',
                    color: 'white',
                    border: 'none',
                    borderRadius: '50%',
                    width: '20px',
                    height: '20px',
                    fontSize: '12px',
                    cursor: 'pointer'
                  }}
                >
                  X
                </button>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}

export default TrickSection;
