import React, { useState } from 'react';

function StickyNoteBoard() {
  const [notes, setNotes] = useState([]);
  const [newNote, setNewNote] = useState('');
  const [noteColor, setNoteColor] = useState('#ffff99'); // Default yellow

  const colors = [
    { value: '#ffff99', label: 'Yellow' },
    { value: '#ff9999', label: 'Pink' },
    { value: '#99ff99', label: 'Green' },
    { value: '#9999ff', label: 'Blue' },
    { value: '#ffcc99', label: 'Orange' }
  ];

  const addNote = () => {
    if (newNote.trim()) {
      setNotes([...notes, { text: newNote, color: noteColor }]);
      setNewNote('');
    }
  };

  const removeNote = (index) => {
    const updatedNotes = [...notes];
    updatedNotes.splice(index, 1);
    setNotes(updatedNotes);
  };

  return (
    <div className="container">
      <h2>üìù Sticky Notes üìù</h2>
      <div style={{textAlign: 'center', marginBottom: '20px'}}>
        <textarea
          value={newNote}
          onChange={(e) => setNewNote(e.target.value)}
          placeholder="Write your note here..."
          rows="3"
          style={{width: '80%', padding: '10px', margin: '10px 0'}}
        />
        <div>
          <select 
            value={noteColor} 
            onChange={(e) => setNoteColor(e.target.value)}
            style={{marginRight: '10px'}}
          >
            {colors.map(color => (
              <option key={color.value} value={color.value}>
                {color.label}
              </option>
            ))}
          </select>
          <button onClick={addNote}>Add Note</button>
        </div>
      </div>

      <div style={{
        display: 'flex',
        flexWrap: 'wrap',
        justifyContent: 'center',
        gap: '15px'
      }}>
        {notes.map((note, index) => (
          <div key={index} style={{
            backgroundColor: note.color,
            padding: '15px',
            width: '150px',
            minHeight: '150px',
            boxShadow: '5px 5px 7px rgba(33,33,33,.7)',
            transform: `rotate(${Math.random() * 10 - 5}deg)`,
            position: 'relative',
            display: 'flex',
            flexDirection: 'column',
            justifyContent: 'space-between'
          }}>
            <div style={{wordWrap: 'break-word'}}>
              {note.text}
            </div>
            <button 
              onClick={() => removeNote(index)}
              style={{
                position: 'absolute',
                bottom: '5px',
                right: '5px',
                backgroundColor: 'transparent',
                border: 'none',
                color: '#ff0000',
                cursor: 'pointer',
                fontWeight: 'bold'
              }}
            >
              X
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default StickyNoteBoard;
