import React, { useState } from 'react';

function StatusUpdate() {
  const [status, setStatus] = useState('');
  const [emoji, setEmoji] = useState('üòä');
  const [updates, setUpdates] = useState([]);

  const handleStatusChange = (e) => {
    setStatus(e.target.value);
  };

  const handleEmojiChange = (e) => {
    setEmoji(e.target.value);
  };

  const addStatus = () => {
    if (status.trim()) {
      const newUpdate = {
        emoji,
        text: status,
        timestamp: new Date().toLocaleString()
      };
      setUpdates([newUpdate, ...updates]);
      setStatus('');
    }
  };

  return (
    <div className="container">
      <h2>‚ú® Mood Ring ‚ú®</h2>
      <div style={{textAlign: 'center'}}>
        <input
          type="text"
          value={status}
          onChange={handleStatusChange}
          placeholder="How are you feeling today?"
          style={{width: '60%'}}
        />
        <select value={emoji} onChange={handleEmojiChange} style={{marginLeft: '10px'}}>
          <option value="üòä">üòä</option>
          <option value="üò¢">üò¢</option>
          <option value="üò°">üò°</option>
          <option value="‚ù§Ô∏è">‚ù§Ô∏è</option>
          <option value="üò¥">üò¥</option>
          <option value="üéÆ">üéÆ</option>
          <option value="üçï">üçï</option>
        </select>
        <button onClick={addStatus} style={{marginLeft: '10px'}}>Update</button>
      </div>

      <div style={{marginTop: '20px', textAlign: 'left'}}>
        <h3 style={{backgroundColor: '#ffccff', padding: '5px'}}>Recent Updates:</h3>
        {updates.length === 0 ? (
          <p style={{textAlign: 'center'}}>No updates yet. Be the first!</p>
        ) : (
          updates.map((update, index) => (
            <div key={index} style={{
              margin: '10px 0',
              padding: '10px',
              backgroundColor: '#ffffff',
              border: '1px solid #cccccc'
            }}>
              <p><strong>{update.emoji} {update.text}</strong></p>
              <p style={{fontSize: '12px', color: '#666666'}}>{update.timestamp}</p>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

export default StatusUpdate;
